import React, { useState, useEffect } from 'react';
import { ListGroup, ListGroupItem } from 'react-bootstrap';

const Chat = () => {
	const [message, setMessage] = useState('');
	const [chatHistory, setChatHistory] = useState([]);
	const [selectedFriend, setSelectedFriend] = useState(null);
	const [chatData, setChatData] = useState(null);

	const backendURL = 'http://localhost:8000/chats/'
	const handleSendMessage = () => {
		if (message.trim() !== '') {
			setChatHistory(prevChatHistory => [...prevChatHistory, message]);
			setMessage('');
		}
	};


	useEffect(() => {
		fetch(backendURL, {
		  method: 'GET',
		  headers: {
			'Content-Type': 'application/json',
			'Authorization': `Token ${localStorage.getItem('Token')}` // Include the token in the Authorization header
		  },
		})
		.then(response => response.json())
		.then(data => {
		  setChatData(data);
		})
		.catch((error) => {
		  console.error('Error:', error);
		});
	  }, []); // Empty array means this effect runs once on mount

	  const handleFriendClick = (chat) => {
		setSelectedFriend(chat);
	  };


	  return (
		<div className="row">
		  <div className="col-md-4">
			<h1>Friends List</h1>
			<ListGroup>
			  {chatData ? chatData.map((chat, index) => (
				<ListGroupItem key={index} onClick={() => handleFriendClick(chat)}>
				  {chat.participant2.username}
				</ListGroupItem>
			  )) : "Loading..."}
			</ListGroup>
		  </div>
		  <div className="col-md-8">
			{selectedFriend && (
				<div>
				<h1>Chat with {selectedFriend}</h1>
				<div>
					{
					selectedFriend.messages.map((message, index) => (
						<p key={index}>{message}</p>
					))}
				</div>
				<input
					type="text"
					value={message}
					onChange={e => setMessage(e.target.value)}
				/>
				<button onClick={handleSendMessage}>Send</button>
				</div>
			)}
			</div>
		</div>
	  );
};

export default Chat;
