import React, { useState, useEffect } from 'react';

import { Button, Card, Container } from 'react-bootstrap';

const OnlineTournament = () => {
	const backendURL = 'http://localhost:8000/tournaments/'
	const [tournaments, setTournaments] = useState([]);
	const [userData, setUserData] = useState({});

	useEffect(() => {
		fetchTournaments();
	}, []);

	useEffect(() => {
		fetchUserData();
	}, []);

	const fetchUserData = () => {
		fetch('http://localhost:8000/auth/user/', {
			method: 'GET',
			headers: {
			  'Content-Type': 'application/json	',	
			  'Authorization': `Token ${localStorage.getItem('Token')}`
			},
			})
			.then(response => response.json())		
			.then(data => {
				setUserData(data);
			})
			.catch((error) => {
				console.error('Error:', error);
			}
		);
	};	

	const fetchTournaments = () => {

		fetch(backendURL, {
			method: 'GET',
			headers: {
			  'Content-Type': 'application/json',
			  'Authorization': `Token ${localStorage.getItem('Token')}` // Include the token in the Authorization header
			},
		  })
		  .then(response => response.json())
		  .then(data => {
			setTournaments(data);
		  })
		  .catch((error) => {
			console.error('Error:', error);
		  });

	};

	const createTournament = () => {	
		fetch(backendURL, {
			method: 'POST',
			headers: {
			  'Content-Type': 'application/json',
			  'Authorization': `Token ${localStorage.getItem('Token')}` // Include the token in the Authorization header
			},
		  })
		  .then(response => response.json())
		  .then(data => {
			window.location.reload();
		  })
		  .catch((error) => {
			console.error('Error:', error);
		  });
	}

	return (
		<Container>
			<h1>Online Tournaments</h1>
			{tournaments.length > 0 ? (
			tournaments.map((tournament) => (
				<Card key={tournament.id} className="mb-3">
				<Card.Body>
					<Card.Title>{tournament.name}</Card.Title>
					<Card.Text>{tournament.description}</Card.Text>
					{tournament.registered ? (
					<>
						<Card.Text>Registered</Card.Text>
						<Button variant="danger" >Unregister</Button>
					</>
					) : null}
				</Card.Body>
				</Card>
			))
			) : (
			<p>No tournaments available.</p>
			)}
			<Button variant="primary" className="mr-2" onClick={createTournament}>Create Tournament</Button>
			<Button variant="secondary">Back to Menu</Button>
		</Container>
	);
};

export default OnlineTournament;
