import React, { useState, useEffect } from 'react';
import { Button, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const Tournament = () => {
  const [showNewTournamentForm, setShowNewTournamentForm] = useState(false);
  const [playerNames, setPlayerNames] = useState(['', '', '', '']);
  const [tournamentStarted, setTournamentStarted] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const tournamentStatus = localStorage.getItem('tournament');
    setTournamentStarted(tournamentStatus === 'true');
  }, []);

  const handleBack = () => {
    navigate('/');
  };

  const handleChange = (index, name) => {
    const updatedPlayerNames = [...playerNames];
    updatedPlayerNames[index] = name;
    setPlayerNames(updatedPlayerNames);
  };

  const handleSubmitNewTournament = (e) => {
    e.preventDefault();
    localStorage.setItem('player1Name', playerNames[0]);
    localStorage.setItem('player2Name', playerNames[1]);
    localStorage.setItem('player3Name', playerNames[2]);
    localStorage.setItem('player4Name', playerNames[3]);
    localStorage.setItem('tournament', true);
    setTournamentStarted(true);
  };

  const handleStartGame = () => {
    // Logik f端r das Starten des Spiels hier einf端gen
  };

  const renderPairs = () => {
    const playerPairs = [];
    const shuffledPlayers = [...playerNames].sort(() => Math.random() - 0.5);
    for (let i = 0; i < shuffledPlayers.length; i += 2) {
      playerPairs.push([shuffledPlayers[i], shuffledPlayers[i + 1]]);
    }
    return playerPairs;
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="text-center">
        {tournamentStarted ? (
          <>
            {renderPairs().map((pair, index) => (
              <div key={index}>
                <div>{pair[0]} VS {pair[1]}</div>
                <Button variant="primary" onClick={handleStartGame}>Start Game</Button>
              </div>
            ))}
            <Button variant="primary" size="lg" onClick={() => { localStorage.removeItem('tournament'); setTournamentStarted(false); }}>New Tournament</Button>
          </>
        ) : (
          <>
            {showNewTournamentForm ? (
              <Form onSubmit={handleSubmitNewTournament}>
                {playerNames.map((name, index) => (
                  <Form.Group key={index}>
                    <Form.Label>Player {index + 1} Name:</Form.Label>
                    <Form.Control
                      type="text"
                      value={name}
                      onChange={(e) => handleChange(index, e.target.value)}
                      required
                    />
                  </Form.Group>
                ))}
                <Button variant="primary" type="submit">
                  Start Tournament
                </Button>
              </Form>
            ) : (
              <Button
                variant="primary"
                size="lg"
                onClick={() => setShowNewTournamentForm(true)}
              >
                New Tournament
              </Button>
            )}
          </>
        )}
        <Button variant="primary" size="lg" onClick={handleBack}>
          Back to Menu
        </Button>
      </div>
    </div>
  );
};

export default Tournament;
