import { BrowserRouter as Router, Routes, Route, Link} from 'react-router-dom';
import PingPongGame from './PingPongGame';
import Tournament from './Tournament';
import Chat from './Chat';
import './css/App.css';
import Login from './Login';
import Dashboard from './Dashboard';
import OnlineGame from './OnlineGame';
import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    setIsLoggedIn(!!localStorage.getItem('Token'));
  }, []);



  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={
              <>
                <header>
                  <h1>ft_transcendence</h1>
                </header>
                <div className="start-page d-flex justify-content-center align-items-center vh-100">
                  <div className="button-container">
                  {isLoggedIn ? (
                      <Link to={`/dashboard/${localStorage.getItem('id')}/`}>
                        <button className="btn btn-primary m-2" style={{width: '18rem', height: '2rem'}}>
                          Profile
                        </button>
                      </Link>
                    ) : (
                      <Link to="/login">
                        <button className="btn btn-primary m-2" style={{width: '18rem', height: '2rem'}}>
                          Login
                        </button>
                      </Link>
                    )}
                    <Link to="/game">
                      <button className="btn btn-primary m-2" style={{width: '18rem', height: '2rem'}}>
                        local game
                      </button>
                    </Link>
                    {isLoggedIn && (<Link to="/OnlineGame">
                      <button className="btn btn-primary m-2" style={{width: '18rem', height: '2rem'}}>
                        online game
                      </button>
                    </Link>)}
                    <Link to="/tournament">
                      <button className="btn btn-primary m-2" style={{width: '18rem', height: '2rem'}}>
                        tournament
                      </button>
                    </Link>
                    <Link to="/">
                      <button className="btn btn-primary m-2" style={{width: '18rem', height: '2rem'}}>
                        placeholder
                      </button>
                    </Link>

                  </div>
                </div>
              </>
            }
          />
          <Route path="/game" element={<PingPongGame />} />
          <Route path='/tournament' element={<Tournament />} />
          <Route path="/login" element={<Login />} />
          <Route path="/dashboard/:id" element={<Dashboard />} />
          <Route path="/chat" element={<Chat />} />
          <Route path="/OnlineGame" element={<OnlineGame />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
