import React, { useState, useEffect } from 'react';

const Dashboard = () => {
  const [displayName, setDisplayName] = useState('');
  const [alias, setAlias] = useState('');
  const [email, setEmail] = useState('');
  const [friends, setFriends] = useState([]);
  const [matchHistory, setMatchHistory] = useState([]);

  // Fetch user information and match history from backend
  useEffect(() => {
    // Lese die lokal gespeicherten Daten
    const savedDisplayName = localStorage.getItem('username');
    const savedAlias = localStorage.getItem('alias');
	const savedEmail = localStorage.getItem('email');
    const savedFriends = JSON.parse(localStorage.getItem('friends')) || [];
    const savedMatchHistory = JSON.parse(localStorage.getItem('matchHistory')) || [];

    // Setze die Zustände mit den lokal gespeicherten Daten
    setDisplayName(savedDisplayName || '');
    setAlias(savedAlias || '');
    setFriends(savedFriends);
	setEmail(savedEmail || '');
    setMatchHistory(savedMatchHistory);
  }, []);

  const handleFriendAdd = (friend) => {
    const updatedFriends = [...friends, friend];
    setFriends(updatedFriends);
    // Speichere die aktualisierten Freunde lokal
    localStorage.setItem('friends', JSON.stringify(updatedFriends));
  };

  return (
    <div>
      <h1>Welcome to your profile</h1>
      <p>Name: {displayName}</p>
      <p>Alias: {alias}</p>
	  <p>Email: {email}</p>
      <h2>Friends</h2>
      <ul>
        {friends.map((friend, index) => (
          <li key={index}>{friend}</li>
        ))}
      </ul>
      <h2>Match History</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Opponent</th>
            <th>Result</th>
          </tr>
        </thead>
        <tbody>
          {matchHistory.map((match, index) => (
            <tr key={index}>
              <td>{match.date}</td>
              <td>{match.opponent}</td>
              <td>{match.result}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
