import React, { useState, useEffect, useRef } from 'react';
import { ListGroup, ListGroupItem } from 'react-bootstrap';

let socket = null;

const Chat = () => {
	const [message, setMessage] = useState('');

	const [selectedFriend, setSelectedFriend] = useState(null);
	const [chatData, setChatData] = useState(null);

	const backendURL = 'http://localhost:8000/chats/'

	const messagesEndRef = useRef(null);

	const scrollToBottom = () => {
	messagesEndRef.current?.scrollIntoView({ behavior: "smooth" })
	}

useEffect(scrollToBottom, [selectedFriend]);

	const handleSendMessage = () => {
		if (message !== '') {
		  // Check if the WebSocket connection is open
		  if (socket && socket.readyState === WebSocket.OPEN) {
			// Send the message
			socket.send(JSON.stringify({ message: message }));

			// Clear the message input
			setMessage('');
		  } else {
			console.error('Cannot send message, WebSocket connection is not open');
		  }
		}
	  };

	const reloadData = () => {

		fetch(backendURL, {
			method: 'GET',
			headers: {
			  'Content-Type': 'application/json',
			  'Authorization': `Token ${localStorage.getItem('Token')}` // Include the token in the Authorization header
			},
		  })
		  .then(response => response.json())
		  .then(data => {
			setChatData(data);
			if (selectedFriend) {
				const sameFriend = data.find(friend => friend.id === selectedFriend.id);
				setSelectedFriend(sameFriend);
			  }
		  })
		  .catch((error) => {
			console.error('Error:', error);
		  });

	};

	useEffect(() => {
		fetch(backendURL, {
		  method: 'GET',
		  headers: {
			'Content-Type': 'application/json',
			'Authorization': `Token ${localStorage.getItem('Token')}` // Include the token in the Authorization header
		  },
		})
		.then(response => response.json())
		.then(data => {
		  setChatData(data);
		})
		.catch((error) => {
		  console.error('Error:', error);
		});
	  }, []); // Empty array means this effect runs once on mount

	  const handleFriendClick = (chat) => {
		setSelectedFriend(chat);

		// If the WebSocket connection is already open, close it
		if (socket && socket.readyState === WebSocket.OPEN) {
		  socket.close();
		}

		// Create a new WebSocket connection
		socket = new WebSocket('ws://localhost:8000/ws/chat/' + chat.participant2.id + '/?token=' + localStorage.getItem('Token'));

		// Connection opened
		socket.addEventListener('open', (event) => {

		  });

		// Listen for messages
		socket.addEventListener('message', (event) => {
		  reloadData();

		});

		// Connection closed
		socket.addEventListener('close', (event) => {
		  console.log('Server connection closed: ', event.code);
		});

		// Connection error
		socket.addEventListener('error', (event) => {
		  console.error('WebSocket error: ', event);
		});
	  };


	  return (
		<div className="row">
		  <div className="col-md-4">
			<h1>Friends List</h1>
			<ListGroup>
			  {chatData ? chatData.map((chat, index) => (
				<ListGroupItem key={index} onClick={() => handleFriendClick(chat)}>
				  {chat.participant2.username}
				</ListGroupItem>
			  )) : "Loading..."}
			</ListGroup>
		  </div>
		  <div className="col-md-8">
			{selectedFriend && (
				<div>
				<h1>Chat with </h1>
				<div style={{ height: '600px', overflow: 'auto'}}>
					{
					selectedFriend.messages.map((message, index) => (
						<p key={index}>{message.text}</p>
					))}
				</div>
				<div >
				<input style={{ width: '60%' }}
					type="text"
					value={message}
					onChange={e => setMessage(e.target.value)}
				/>
				<button style={{ height: '30px'}}onClick={handleSendMessage}>Send</button>
				</div>
				</div>
			)}
			</div>
		</div>
	  );
};

export default Chat;
