import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Dashboard = () => {
  const [userData, setUserData] = useState(null); // Zustand f체r Benutzerdaten
  let { id } = useParams();
  useEffect(() => {
    const backendURL = 'http://localhost:8000/users/';
    const userId = localStorage.getItem('id');


    if (!userId) {
      console.error('Benutzer-ID nicht im localStorage gefunden');
      return;
    }

    const requestURL = `${backendURL}${id}/`;

    fetch(requestURL, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
    .then(response => {
      if (response.ok) {
        return response.json();
      } else {
        throw new Error('Es ist ein Fehler aufgetreten. Bitte versuchen Sie es sp채ter erneut.');
      }
    })
    .then(data => {
      setUserData(data); // Benutzerdaten im Zustand speichern
    })
    .catch(error => {
      console.error('Fehler beim Senden der Registrierungsdaten:', error);
      alert(error.message);
    });
  }, [id]);
  useEffect(() => {
    console.log('ID:', id); // Check the id
    fetch(`http://localhost:8000/user/${id}/`)
      .then(response => response.json())
      .then(data => {
        console.log('API Response:', data); // Check the API response
        setUserData(data);
      })
      .catch(error => {
        console.error('Error:', error);
      });
  }, [id]);

  console.log('User Data:', userData); // Check the userData state

  return (
    <div>
      <h1>Welcome to your profile</h1>
      <p>This is the Dashboard component</p> // Check if the component is rendered
      {userData && (
        <div>
          <p>Name: {userData.username}</p>
          <p>Email: {userData.email}</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
