import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Card, ListGroup } from 'react-bootstrap';
/* eslint-disable */
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [userData, setUserData] = useState(null); // Zustand für Benutzerdaten
  let { id } = useParams();
  const navigate = useNavigate();
  const [isEditingProfile, setIsEditingProfile] = useState(false);
  onst [newProfileData, setNewProfileData] = useState({
    image: '',
    username: '',
    email: '',
    password: '',
  });

  useEffect(() => {
    if (userData) {
      setNewProfileData({
        image: userData.profile.image,
        username: userData.username,
        email: userData.email,
        password: '',
      });
    }
  }, [userData]);

  const handleProfileDataChange = (event) => {
    setNewProfileData({
      ...newProfileData,
      [event.target.name]: event.target.value,
    });
  };

  const handleProfileUpdate = () => {
    fetch(`http://localhost:8000/users/${userData.id}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Token ${localStorage.getItem('Token')}`,
      },
      body: JSON.stringify(newProfileData),
    })
      .then(response => response.json())
      .then(data => {
        setUserData(data);
        setIsEditingProfile(false);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  function fetchUserData() {
    const backendURL = 'http://localhost:8000/users/';
    const userId = localStorage.getItem('id');

    if (!userId) {
      console.error('Benutzer-ID nicht im localStorage gefunden');
      return;
    }

    const requestURL = `${backendURL}${userId}/`;

    fetch(requestURL, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Token ${localStorage.getItem('Token')}` // Include the token in the Authorization header
      },
    })
    .then(response => response.json())
    .then(data => {
      setUserData(data);
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  }

  useEffect(() => {
    const backendURL = 'http://localhost:8000/users/';
    const userId = localStorage.getItem('id');


    if (!userId) {
      console.error('Benutzer-ID nicht im localStorage gefunden');
      return;
    }

    const requestURL = `${backendURL}${id}/`;

    fetch(requestURL, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Token ${localStorage.getItem('Token')}` // Include the token in the Authorization header
      },
    })
    .then(response => {
      if (response.ok) {
        return response.json();
      } else {
        throw new Error('Es ist ein Fehler aufgetreten. Bitte versuchen Sie es später erneut.');
      }
    })
    .then(data => {
      setUserData(data); // Benutzerdaten im Zustand speichern
    })
    .catch(error => {
      console.error('Fehler beim Senden der Registrierungsdaten:', error);
      alert(error.message);
    });
  }, [id]);

  function addFriend() {
    fetch('http://localhost:8000/friend-requests/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Token ${localStorage.getItem('Token')}` // Include the token in the Authorization header
      },
      body: JSON.stringify({
        'from_user': parseInt(localStorage.getItem('id'), 10),
        'to_user': userData.id,
      }),
    })
    .then(response => response.json())
    .then(data => {
      fetchUserData();  
      console.log('Success:', data);
      console.log(localStorage.getItem('id'));
      console.log(localStorage.getItem('Token'));
      console.log(userData.id);
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  }

  function acceptFriendRequest(requestId) {


    fetch(`http://localhost:8000/friend-requests/${requestId}/accept/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application  /json',   
        'Authorization': `Token ${localStorage.getItem('Token')}` // Include the token in the Authorization header  
      },  
    })
    fetchUserData();

    // Send a request to the backend to accept the friend request
    // Then fetch the userData again to update the list of friend requests
  }
  const handleBack = () => {


      navigate('/'); // Navigiere zur Startseite, wenn der "Zurück"-Button geklickt wird
      window.location.reload();
    };

    const handleOut = () => {
      localStorage.removeItem('Token'); 
      localStorage.removeItem('id');
      navigate('/'); // Navigiere zur Startseite, wenn der "Zurück"-Button geklickt wird
      window.location.reload();
    };

    const changeProfil = () => {
      if (isEditingProfile) {
        setIsEditingProfile(false);
        return;
      }
      setIsEditingProfile(true);
    };

  return (
    <div className="container mt-5">
      {userData && !isEditingProfile ? (
        <>
          {userData.profile.is_current_user && <h1>Welcome to your profile, {userData.username}</h1>}
          <Card style={{ width: '18rem'}}>
            <Card.Img variant="top" src={userData.profile.image} />
            <Card.Body>
              <Card.Title>{userData.username}</Card.Title>
              {userData.profile.is_online ? 'Online' : 'Offline'}
              <Card.Text>
                Email: {userData.email}
              </Card.Text>
            </Card.Body>
            <ListGroup variant="flush">
              <ListGroup.Item>Games Won: {userData.profile.games_won}</ListGroup.Item>
              <ListGroup.Item>Games Lost: {userData.profile.games_lost}</ListGroup.Item>
            </ListGroup>
            <Card.Body style={{ overflow: 'auto', maxHeight: '100px' }}>
              Friends:
              <ul>
              {userData.profile.friends.map((friend, index) => (
                <li key={index}>
                  <a href={`/dashboard/${friend.id}`}>{friend.username}</a>
                </li>
              ))}
            </ul>
              {!userData.profile.is_current_user && !userData.profile.already_sent_request && <button onClick={addFriend}>Add Friend</button>}
              {userData.profile.is_current_user && <div>Friend Requests:
            <ul>
              {userData.profile.to_user.map((request, index) => (
                <li key={index}>
                  {request.from_user_username}
                  <button onClick={() => acceptFriendRequest(request.id)}>Accept</button>
                </li>
              ))}
            </ul></div>}
            </Card.Body>
              Game History
              <Card.Body style={{ overflow: 'auto', maxHeight: '100px', display: 'flex', flexDirection: 'column' }}>
              <ul style={{ flex: 1, display: 'flex', flexDirection: 'column', width: '100%' }}>
                {userData.profile.games.map((game, index) => (
                  <li key={index} style={{ flex: 1, width: '100%' }}>
                    {new Date(game.created).toLocaleString()} | vs {game.opponent_username} | {game.p1_score} : {game.p2_score} {game.is_winner ? 'won' : 'lost'}
                  </li>
                ))}
              </ul>
            </Card.Body>
          </Card>
          {userData.profile.is_current_user && <div> <button style={{width: '18rem', height: "2rem"}} onClick={changeProfil}>Edit Profile</button></div>}
          <button style={{width: '18rem', height: '4rem'}} onClick={handleBack}>Back to menu</button>
          <button style={{width: '18rem', height: '4rem'}} onClick={handleOut}>Log Out</button>
        </>

      ) : (
        <div>
              {/* <input type="file" name="image" onChange={handleProfileDataChange} />
              <input type="text" name="username" value={newProfileData.username} onChange={handleProfileDataChange} />
              <input type="email" name="email" value={newProfileData.email} onChange={handleProfileDataChange} />
              <input type="password" name="password" value={newProfileData.password} onChange={handleProfileDataChange} /> */}
              <button onClick={changeProfil}>Update Profile</button>
              <button onClick={changeProfil}>Cancel</button>
            </div>
      )}
    </div>
  );}

export default Dashboard;
