import React, { useState } from 'react';
import { Button, Form } from 'react-bootstrap';
/* eslint-disable */
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';

const Tournament = ({ hasTournament }) => {
  const [showNewTournamentForm, setShowNewTournamentForm] = useState(false);
  const [playerNames, setPlayerNames] = useState(['', '', '', '']);
  const navigate = useNavigate();
  const handleBack = () => {
    // Navigiere zur Startseite, wenn der "Zurück"-Button geklickt wird
    navigate('/');
  };

  const handleChange = (index, name) => {
    const updatedPlayerNames = [...playerNames];
    updatedPlayerNames[index] = name;
    setPlayerNames(updatedPlayerNames);
  };

  const handleSubmitNewTournament = (e) => {
    e.preventDefault(); // Verhindert das Standardverhalten des Formulars
    localStorage.setItem('player1Name', e.target.player1.value);
    localStorage.setItem('player2Name', e.target.player2.value);
    localStorage.setItem('player3Name', e.target.player3.value);
    localStorage.setItem('player4Name', e.target.player4.value);
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="text-center">
        {hasTournament ? (
          <>
            <Button variant="primary" size="lg" className="mb-3">
              continue tournament
            </Button>
            <br />
          </>
        ) : (
          <>
            {showNewTournamentForm ? (
              <Form onSubmit={handleSubmitNewTournament}>
                {playerNames.map((name, index) => (
                  <Form.Group key={index}>
                    <Form.Label>Player {index + 1} Name:</Form.Label>
                    <Form.Control
                      type="text"
                      value={name}
                      onChange={(e) => handleChange(index, e.target.value)}
                      required
                    />
                  </Form.Group>
                ))}
                <Button variant="primary" type="submit">
                  Start Tournament
                </Button>
              </Form>
            ) : (
              <Button
                variant="primary"
                size="lg"
                onClick={() => setShowNewTournamentForm(true)}
              >
                new tournament
              </Button>
            )}
          </>
        )}
        <Button variant="primary" size="lg" onClick={handleBack}>
          back to menu
        </Button>
      </div>
    </div>
  );
};

export default Tournament;
