import React, { useState, useEffect } from 'react';

const Dashboard = () => {
  const [displayName, setDisplayName] = useState('');
  const [alias, setAlias] = useState('');
  const [email, setEmail] = useState('');
  const [friends, setFriends] = useState([]);
  const [matchHistory, setMatchHistory] = useState([]);

  // Fetch user information and match history from backend
  useEffect(() => {

    const backendURL = 'http://localhost:8000/user/';
const userId = localStorage.getItem('userId'); // Annahme: Die Benutzer-ID ist unter dem Schlüssel 'userId' im localStorage gespeichert

if (!userId) {
  // Fehlerbehandlung, falls die Benutzer-ID nicht im localStorage gefunden wurde
  console.error('Benutzer-ID nicht im localStorage gefunden');
  return; // Beenden Sie die Funktion oder geben Sie eine Fehlermeldung aus, wie Sie es wünschen
}

const requestURL = `${backendURL}${userId}`;

// Jetzt können Sie requestURL verwenden, um eine Anfrage an das Backend zu senden
// Zum Beispiel: fetch(requestURL, { method: 'GET' })


    fetch(requestURL, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    })
    .then(response => {
    if (response.ok) {
      // Die Antwort des Servers in JSON umwandeln
      return response.json();
    } else {
      // Wenn die Antwort nicht ok ist, zeige eine Fehlermeldung an
      throw new Error('Es ist ein Fehler aufgetreten. Bitte versuchen Sie es später erneut.');
    }
    })
    .then(data => {
    // Den Token aus der Antwort extrahieren
    const token = data.token;
    const id = data.user.id;

    // Den Token lokal speichern
    localStorage.setItem('Token', token);
    localStorage.setItem('id', id);

    // Weiterleiten oder andere Aktionen ausführen
    navigate('/dashboard');
    })
    .catch(error => {
      // Fehlerbehandlung, falls ein Fehler auftritt
      console.error('Fehler beim Senden der Registrierungsdaten:', error);
      alert(error.message); // Fehlermeldung anzeigen
    });

  }, []);

  const handleFriendAdd = (friend) => {
    const updatedFriends = [...friends, friend];
    setFriends(updatedFriends);
    // Speichere die aktualisierten Freunde lokal
    localStorage.setItem('friends', JSON.stringify(updatedFriends));
  };

  return (
    <div>
      <h1>Welcome to your profile</h1>
      <p>Name: {displayName}</p>
      <p>Alias: {alias}</p>
	  <p>Email: {email}</p>
      <h2>Friends</h2>
      <ul>
        {friends.map((friend, index) => (
          <li key={index}>{friend}</li>
        ))}
      </ul>
      <h2>Match History</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Opponent</th>
            <th>Result</th>
          </tr>
        </thead>
        <tbody>
          {matchHistory.map((match, index) => (
            <tr key={index}>
              <td>{match.date}</td>
              <td>{match.opponent}</td>
              <td>{match.result}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
