import React, { useState, useEffect } from 'react';
import { ListGroup, ListGroupItem } from 'react-bootstrap';

const Chat = () => {
	const [message, setMessage] = useState('');

	const [selectedFriend, setSelectedFriend] = useState(null);
	const [chatData, setChatData] = useState(null);

	const backendURL = 'http://localhost:8000/chats/'

	const handleSendMessage = () => {
		if (message !== '') {
		  // Check if the WebSocket connection is open
		  if (socket && socket.readyState === WebSocket.OPEN) {
			// Send the message
			socket.send(message);

			// Clear the message input
			setMessage('');
		  } else {
			console.error('Cannot send message, WebSocket connection is not open');
		  }
		}
	  };
	let socket = null;

	useEffect(() => {
		fetch(backendURL, {
		  method: 'GET',
		  headers: {
			'Content-Type': 'application/json',
			'Authorization': `Token ${localStorage.getItem('Token')}` // Include the token in the Authorization header
		  },
		})
		.then(response => response.json())
		.then(data => {
		  setChatData(data);
		})
		.catch((error) => {
		  console.error('Error:', error);
		});
	  }, []); // Empty array means this effect runs once on mount

	  const handleFriendClick = (chat) => {
		setSelectedFriend(chat);

		// If the WebSocket connection is already open, close it
		if (socket && socket.readyState === WebSocket.OPEN) {
		  socket.close();
		}

		// Create a new WebSocket connection
		socket = new WebSocket('ws://localhost:8000/ws/chat/' + chat.participant2.id + '/?token=' + localStorage.getItem('Token'));

		// Connection opened
		socket.addEventListener('open', (event) => {
			socket.send(JSON.stringify({ message: 'Hello Server!' }));
		  });

		// Listen for messages
		socket.addEventListener('message', (event) => {
		  console.log('Message from server: ', event.data);
		});

		// Connection closed
		socket.addEventListener('close', (event) => {
		  console.log('Server connection closed: ', event.code);
		});

		// Connection error
		socket.addEventListener('error', (event) => {
		  console.error('WebSocket error: ', event);
		});
	  };


	  return (
		<div className="row">
		  <div className="col-md-4">
			<h1>Friends List</h1>
			<ListGroup>
			  {chatData ? chatData.map((chat, index) => (
				<ListGroupItem key={index} onClick={() => handleFriendClick(chat)}>
				  {chat.participant2.username}
				</ListGroupItem>
			  )) : "Loading..."}
			</ListGroup>
		  </div>
		  <div className="col-md-8">
			{selectedFriend && (
				<div>
				<h1>Chat with </h1>
				<div>
					{
					selectedFriend.messages.map((message, index) => (
						<p key={index}>{message.text}</p>
					))}
				</div>
				<input
					type="text"
					value={message}
					onChange={e => setMessage(e.target.value)}
				/>
				<button onClick={handleSendMessage}>Send</button>
				</div>
			)}
			</div>
		</div>
	  );
};

export default Chat;
