import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Card, Container } from 'react-bootstrap';

const OnlineTournament = () => {
	const [tournaments, setTournaments] = useState([]);

	useEffect(() => {
		fetchTournaments();
	}, []);

	const fetchTournaments = async () => {
		try {
			const response = await axios.get('/api/tournaments'); // Hier die entsprechende Backend-URL einfügen
			setTournaments(response.data);
		} catch (error) {
			console.error('Error fetching tournaments:', error);
		}
	};

	return (
		<Container>
			<h1>Online Tournaments</h1>
			{tournaments.length > 0 ? (
				tournaments.map((tournament) => (
					<Card key={tournament.id} className="mb-3">
						<Card.Body>
							<Card.Title>{tournament.name}</Card.Title>
							<Card.Text>{tournament.description}</Card.Text>
						</Card.Body>
					</Card>
				))
			) : (
				<p>No tournaments available.</p>
			)}
			<Button variant="primary" className="mr-2">Create Tournament</Button>
			<Button variant="secondary">Back to Menu</Button>
		</Container>
	);
};

export default OnlineTournament;
