import React, { useState, useEffect } from 'react';
import { useLocation, useParams } from 'react-router-dom';

const Dashboard = () => {
  const [userData, setUserData] = useState(null);
  const { userId } = useParams(); // Benutzer-ID aus den Routenparametern erhalten
  const location = useLocation(); // Aktuelle Location verwenden, um die Benutzer-ID in der URL anzuzeigen

  useEffect(() => {
    if (!userId) {
      console.error('Benutzer-ID nicht in den Routenparametern gefunden');
      return;
    }

    const backendURL = 'http://localhost:8000/user/';
    const requestURL = `${backendURL}${userId}/`;

    fetch(requestURL)
      .then(response => {
        if (!response.ok) {
          throw new Error('Es ist ein Fehler aufgetreten. Bitte versuchen Sie es später erneut.');
        }
        return response.json();
      })
      .then(data => {
        setUserData(data);
      })
      .catch(error => {
        console.error('Fehler beim Abrufen der Benutzerdaten:', error);
        alert(error.message);
      });
  }, [userId]);

  if (!userData) {
    return <div>Lade Benutzerdaten...</div>;
  }

  return (
    <div>
      <h1>Welcome to your profile</h1>
      <p>UserID in URL: {userId}</p>
      <p>Name: {userData.username}</p>
      <p>Email: {userData.email}</p>
      {/* Weitere Benutzerinformationen anzeigen */}
    </div>
  );
};

export default Dashboard;
