import { BrowserRouter as Router, Routes, Route, Link} from 'react-router-dom';
import PingPongGame from './PingPongGame';
import Tournament from './Tournament';
import Chat from './Chat';
import './css/App.css';
import Login from './Login';
import Dashboard from './Dashboard';
import OnlineGame from './OnlineGame';
import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    setIsLoggedIn(!!localStorage.getItem('Token'));
  }, []);

  return (
    <Router>
      <div className="App">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <div className="container-fluid">
            <a className="navbar-brand" href="/">ft_transcendence</a>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav">
                {isLoggedIn ? (
                  <li className="nav-item">
                    <Link className="nav-link" to={`/dashboard/${localStorage.getItem('id')}/`}>Profile</Link>
                  </li>
                ) : (
                  <li className="nav-item">
                    <Link className="nav-link" to="/login">Login</Link>
                  </li>
                )}
                <li className="nav-item">
                  <Link className="nav-link" to="/game">Local Game</Link>
                </li>
                {isLoggedIn && (
                  <li className="nav-item">
                    <Link className="nav-link" to="/OnlineGame">Online Game</Link>
                  </li>
                )}
                <li className="nav-item">
                  <Link className="nav-link" to="/tournament">Tournament</Link>
                </li>
              </ul>
            </div>
          </div>
        </nav>
        <Routes>
          <Route path="/game" element={<PingPongGame />} />
          <Route path='/tournament' element={<Tournament />} />
          <Route path="/login" element={<Login />} />
          <Route path="/dashboard/:id" element={<Dashboard />} />
          <Route path="/chat" element={<Chat />} />
          <Route path="/OnlineGame" element={<OnlineGame />} />
          <Route path='/' element={<div>Home</div>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
