import React, { useState } from 'react';
/* eslint-disable */
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';
import './css/Login.css';



function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [alias, setAlias] = useState('');
  const [registering, setRegistering] = useState(false);

  const navigate = useNavigate();

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleEmailChange = (event) => {
	setEmail(event.target.value);
  };

  const handleAliasChange = (event) => {
	setAlias(event.target.value);
  };


const handleLogin = () => {
	if (username.trim() === '' || password.trim() === '') {
		alert('Please enter username and password');
		return;
	  }
  // Send a request to the backend to check if the username and password are valid
//   fetch('/api/login', {
//     method: 'POST',
//     headers: {
//       'Content-Type': 'application/json',
//     },
//     body: JSON.stringify({ username, password }),
//   })
//     .then((response) => response.json())
//     .then((data) => {
//       if (data.success) {
//         // Login successful, navigate to the dashboard
//         navigate('/dashboard');
//       } else {
//         // Login failed, display an error message
//         alert('Invalid username or password');
//       }
//     })
//     .catch((error) => {
//       console.error('Error:', error);
//     });
  navigate('/dashboard');
};


const handleRegister = () => {
	// Speichere die eingegebenen Daten lokal
	if (username.trim() === '' || password.trim() === '' || email.trim() === '' || alias.trim() === ''){
		alert('Please enter the required fields');
		return;
	  }
	localStorage.setItem('username', username);
	localStorage.setItem('password', password);
	localStorage.setItem('email', email);
	localStorage.setItem('alias', alias);


	// Leite den Benutzer zur Dashboard-Seite weiter
	navigate('/dashboard');
  };


  const handleBack = () => {
	if (registering === true) {
		setRegistering(false);
		return
	}
    navigate('/'); // Navigiere zur Startseite, wenn der "Zurück"-Button geklickt wird
  };
	return (
		<div className='container'>
			<label>
				Username:
				<input type="text" value={username} onChange={handleUsernameChange} className="input-field"/>
			</label>
			<br />
			<label>
				Password:
				<input type="password" value={password} onChange={handlePasswordChange} className="input-field"/>
			</label>
			<br />
			{!registering && ( <div>
			<button onClick={handleLogin}>Login</button>
			<br />
			<button onClick={() => setRegistering(true)}>Register</button>
			<br /> </div>)}
			{registering && ( <div>
				<label>
				Email:
				<input type="email" value={email} onChange={handleEmailChange} />
			</label>
			<br />
			<label>
				Alias:
				<input type="alias" value={alias} onChange={handleAliasChange} className="input-field"/>
			</label>
			<br />
				<button onClick={handleRegister}>complete register</button> </div>)}
			<button onClick={handleBack}>Back</button>
		</div>
	);
}

export default Login;
