import React, { useEffect, useState } from 'react';

const OnlineGame = () => {
	const [ws, setWs] = useState(null);
	const [players, setPlayers] = useState([
		{ paddleY: 0, upPressed: false, downPressed: false, score: 0 },
		{ paddleY: 0, upPressed: false, downPressed: false, score: 0 },
	]);
  	const [ball, setBall] = useState({ x: 0, y: 0, r: 10 });
	const [playerId, setPlayerId] = useState(null);
  	const [playerNum, setPlayerNum] = useState(0);

	useEffect(() => {

		const websocket = new WebSocket('ws://localhost:8000/ws/my_consumer/');
		setWs(websocket);

		websocket.onopen = () => {
		  console.log('WebSocket connection opened');
		};

		websocket.onclose = () => {
		  console.log('WebSocket connection closed');
		};

		websocket.onerror = (event) => {
		  console.error('WebSocket error:', event);
		};

		websocket.onmessage = (event) => {

		  const message = JSON.parse(event.data);
		  if (message.type === 'playerId') {
			setPlayerId(message.playerId);
			setPlayerNum(message.playerNum);
			console.log('Received playerId:', message.playerId);
		  } else if (message.type === 'game_start') {
			console.log('Received game_start:', message);
		  } else if (message.type === 'game_update') {
			setBall({ x: message.x, y: message.y });
			setPlayers([
			  { ...players[0], paddleY: message.players[0].paddleY, score: message.players[0].score },
			  { ...players[1], paddleY: message.players[1].paddleY, score: message.players[1].score },
			]);
		  } else if (message.type === 'game_over') {
			console.log('Received game_over:', message);
			if (ws) {
				ws.close();
			}
		  }

		//   console.log('Received message:', message);
		};
	}, []);


	return (
		<div style={{ position: 'relative', height: '100vh', width: '100vw' }}>
		  <div className="ball" style={{ position: 'absolute', top: ball.y + 'px', left: ball.x + 'px' }}></div>
		  <div className="paddle" style={{ position: 'absolute', top: players[0].paddleY + 'px', left: '0px', width: '10px', height: '75px' }}></div>
		  <div className="paddle" style={{ position: 'absolute', top: players[1].paddleY + 'px', right: '0px', width: '10px', height: '75px' }}></div>
		</div>
	  );
};

export default OnlineGame;
